import React from 'react';
import headshot from '../assets/headshot.JPG'; // Import your headshot image
import './Profile.css'; // Import the CSS file


const Profile = () => {
    return (
        <div style={styles.profileSection}>
            {/* Profile Image */}
            <img src={headshot} alt="Profile" style={styles.image} />

            {/* Text Section */}
            <div style={styles.textSection}>
                {/* Hello with Line */}
                <div style={styles.helloSection}>
                    <span style={styles.helloText}>Hello,</span>
                    <div style={styles.line}></div>
                </div>

                {/* Large Text */}
                <p style={styles.bodyText}>
                    I'm Chase, a student at NYU studying Mathematics and Computer Science. Scroll down to see my recent projects!
                </p>
            </div>
        </div>
    );
};

const styles = {
    profileSection: {
        display: 'flex',
        alignItems: 'center',
        paddingLeft: '50px',
        paddingRight: '50px',
        paddingBottom: '50px',
        flexDirection: 'row', // Default layout (image on left, text on right)
    },
    image: {
        width: '259.2px', // Set a fixed size for the image
        height: '324px',
        marginRight: '50px', // Space between the image and text
    },
    textSection: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
    },
    helloSection: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: '20px',
    },
    helloText: {
        fontSize: '15px',
        fontStyle: 'italic',
        color: '#2a2829',
        marginRight: '10px',
    },
    line: {
        flex: '100%',
        height: '1px',
        backgroundColor: '#2a2829',
    },
    bodyText: {
        fontSize: '1.5rem',
        color: '#2a2829',
    },
    // Media Query for Small Screens (Mobile)
    '@media (max-width: 768px)': {
        profileSection: {
            flexDirection: 'column', // Stack image and text vertically
            alignItems: 'center', // Center align for mobile
        },
        image: {
            marginRight: '0', // Remove margin when stacked
            marginBottom: '20px', // Add space between image and text
        },
    },
};

export default Profile;
