import React from 'react';

const ProjectCard = ({name, photoUrl, description}) => {
    return (
        <div style={styles.container}>
            <img style={styles.image} src={photoUrl} alt={name} />

            <div style={styles.textBox}>
                <p style={styles.nameText}>
                    {name}
                </p>
                <p style={styles.descriptionText}>
                    {description}
                </p>
            </div>
        </div>
    );
};

const styles = {
    container: {
        backgroundColor: '#2a2829',
        display: 'flex',
        alignItems: 'center',
        marginLeft: '100px',
        marginRight: '100px',
        flexDirection: 'row',
        marginBottom: '50px',
        height: '300px',
    },

    image: {
        marginLeft: '20px',
        marginTop: '50px',
        marginBottom: '50px',
        width: '300px', // Define a width for the image
        height: '100%',
        objectFit: 'cover', // Ensure the image scales properly
        marginRight: '20px', // Add space between the image and text
    },

    textBox: {
        display: 'flex',
        flexDirection: 'column', // Stack name and description vertically
        height: '100%',
    },

    nameText: {
        justifyContent: 'flex-start',
        color: '#e0e0dc',
        fontSize: '20px',
        fontWeight: 'bold',
    },

    descriptionText: {
        justifyContent: 'flex-end',
        color: '#e0e0dc',
        fontSize: '14px',
    },
}

export default ProjectCard;
