import React from 'react';

const ProjectCard = ({ name, photoUrl, description, demoLink, presentationLink }) => {
    return (
        <div style={styles.container}>
            <img style={styles.image} src={photoUrl} alt={name} />
            <div style={styles.textBox}>
                <p style={styles.nameText}>{name}</p>
                <p style={styles.descriptionText}>{description}</p>

                {/* Conditionally render Demo or Presentation buttons */}
                <div style={styles.buttonContainer}>
                    {demoLink && (
                        <a href={demoLink} target="_blank" rel="noopener noreferrer" style={styles.button}>
                            Demo
                        </a>
                    )}
                    {presentationLink && (
                        <a href={presentationLink} target="_blank" rel="noopener noreferrer" style={styles.button}>
                            Presentation
                        </a>
                    )}
                </div>
            </div>
        </div>
    );
};

const styles = {
    container: {
        backgroundColor: '#2a2829',
        display: 'flex',
        alignItems: 'center',
        marginLeft: '100px',
        marginRight: '100px',
        flexDirection: 'row',
        marginBottom: '50px',
        height: '300px',
    },

    image: {
        marginLeft: '20px',
        marginTop: '50px',
        marginBottom: '50px',
        width: '260px', // Define a width for the image
        height: '260px',
        objectFit: 'cover', // Ensure the image scales properly
        marginRight: '20px', // Add space between the image and text
    },

    textBox: {
        display: 'flex',
        flexDirection: 'column', // Stack name and description vertically
        height: '100%',
        padding: '20px',
    },

    nameText: {
        justifyContent: 'flex-start',
        color: '#e0e0dc',
        fontSize: '23px',
        fontWeight: 'bold',
    },

    descriptionText: {
        justifyContent: 'flex-end',
        color: '#e0e0dc',
        fontSize: '23px',
    },

    buttonContainer: {
        marginTop: '20px', // Add some space above the buttons
        marginBottom: '20px',
    },

    button: {
        display: 'inline-block',
        padding: '10px 20px',
        margin: '5px',
        backgroundColor: '#642c95',
        color: '#fff',
        textDecoration: 'none',
        borderRadius: '5px',
        fontSize: '16px',
        textAlign: 'center',
    }
}

export default ProjectCard;
