import React from 'react';

const ProjectCard = ({ name, photoUrl, description, demoLink, presentationLink, website }) => {
    return (
        <div style={styles.container}>
            <img style={styles.image} src={photoUrl} alt={name} />
            <div style={styles.textBox}>
                <p style={styles.nameText}>{name}</p>
                <p style={styles.descriptionText}>{description}</p>

                {/* Conditionally render Demo and Presentation links */}
                <div style={styles.linkContainer}>
                    {demoLink && (
                        <a href={demoLink} target="_blank" rel="noopener noreferrer" style={styles.link}>
                            Demo
                        </a>
                    )}
                    {presentationLink && (
                        <a href={presentationLink} target="_blank" rel="noopener noreferrer" style={styles.link}>
                            Presentation
                        </a>
                    )}

                    {website && (
                        <a href={website} target="_blank" rel="noopener noreferrer" style={styles.link}>
                            Website
                        </a>
                    )}
                </div>
            </div>
        </div>
    );
};

const styles = {
    container: {
        backgroundColor: '#2a2829',
        display: 'flex',
        alignItems: 'center',
        marginLeft: '100px',
        marginRight: '100px',
        flexDirection: 'row',
        marginBottom: '50px',

    },

    image: {
        marginLeft: '20px',
        marginTop: '50px',
        marginBottom: '50px',
        width: '260px', // Define a width for the image
        height: '100%',
        objectFit: 'cover', // Ensure the image scales properly
        marginRight: '20px', // Add space between the image and text
    },

    textBox: {
        display: 'flex',
        flexDirection: 'column', // Stack name and description vertically
        height: '100%',
        padding: '30px',
        marginTop: '30px',
        marginBottom: '30px',
    },

    nameText: {
        justifyContent: 'flex-start',
        color: '#e0e0dc',
        fontSize: '23px',
        fontWeight: 'bold',
    },

    descriptionText: {
        justifyContent: 'flex-end',
        color: '#e0e0dc',
        fontSize: '23px',
    },

    linkContainer: {
        marginTop: '20px', // Add some space above the links
        alignItems: 'bottom',
    },

    link: {
        color: '#e0e0dc',
        fontSize: '20px',
        textDecoration: 'italic',
        fontWeight: 'bold',
        marginRight: '15px', // Space between the links
    }
}

export default ProjectCard;
