import React, { useEffect, useState } from 'react';
import './Header.css';
import Resume from '../assets/Vitale-Resume.pdf';

const Header = () => {
    const [isScrollingDown, setIsScrollingDown] = useState(false);
    const [lastScrollY, setLastScrollY] = useState(0);
    const [isSmallScreen, setIsSmallScreen] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            setIsSmallScreen(window.innerWidth <= 750);
        };

        const handleScroll = () => {
            if (!isSmallScreen) {
                const currentScrollY = window.scrollY;
                setIsScrollingDown(currentScrollY > lastScrollY);
                setLastScrollY(currentScrollY);
            }
        };

        handleResize(); // Set the initial screen size
        window.addEventListener('resize', handleResize);
        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('resize', handleResize);
            window.removeEventListener('scroll', handleScroll);
        };
    }, [lastScrollY, isSmallScreen]);

    return (
        <header className={`header ${isScrollingDown && !isSmallScreen ? 'hidden' : ''}`}>
            {/* Left Side: Website Name */}
            <div className="left">
                <a href="/" className="header-link">
                    Chase C. Vitale
                </a>
            </div>

            {/* Right Side: Links */}
            <div className="right">
                <a href={Resume} className="header-link" target="_blank" rel="noreferrer">
                    Résumé
                </a>
                <a href="https://www.linkedin.com/in/chasecvitale/" target="_blank" rel="noopener noreferrer" className="header-link">
                    LinkedIn
                </a>
                <a href="https://github.com/chasecvitale" target="_blank" rel="noopener noreferrer" className="header-link">
                    GitHub
                </a>
            </div>
        </header>
    );
};

export default Header;
