import React from 'react';

const Languages = () => {
    return (
        <div style={styles.container}>
            <div style={styles.iconItem}>
                <img src="../assets/python.png" alt="Python" style={styles.iconImage} />
                <p style={styles.iconLabel}>Python</p>
            </div>
            <div style={styles.iconItem}>
                <img src="../assets/java.png" alt="Java" style={styles.iconImage} />
                <p style={styles.iconLabel}>Java</p>
            </div>
            <div style={styles.iconItem}>
                <img src="../assets/js.png" alt="JavaScript" style={styles.iconImage} />
                <p style={styles.iconLabel}>JavaScript</p>
            </div>
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',  // Allows icons to move to the next line
        gap: '20px',  // Space between icons
    },
    iconItem: {
        textAlign: 'center',
        transition: 'transform 0.3s ease',  // Smooth transition for scaling
    },
    iconImage: {
        width: '60px',  // Set icon size
        height: '60px',
        transition: 'transform 0.3s ease',  // Smooth transition for scaling
    },
    iconLabel: {
        color: '#e0e0dc',
        fontSize: '14px',
        marginTop: '8px',
    },
    // Hover effect for scaling the icon
    '.iconItem:hover': {
        transform: 'scale(1.1)',  // Slightly increase size on hover
    },
};

export default Languages;
